// LoadPurchaseTransaction loads the purchaseHistory's PurchaseTransaction into the .R struct
func (o *PurchaseHistory) LoadPurchaseTransaction(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.PurchaseTransaction = nil

	related, err := o.PurchaseTransaction(mods...).One(ctx, exec)
	if err != nil {
		return err
	}

	related.R.PurchaseHistories = PurchaseHistorySlice{o}

	o.R.PurchaseTransaction = related
	return nil
}

// LoadPurchaseTransaction loads the purchaseHistory's PurchaseTransaction into the .R struct
func (os PurchaseHistorySlice) LoadPurchaseTransaction(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	purchaseTransactions, err := os.PurchaseTransaction(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range purchaseTransactions {

			if !(o.PurchaseTransactionID == rel.ID) {
				continue
			}

			rel.R.PurchaseHistories = append(rel.R.PurchaseHistories, o)

			o.R.PurchaseTransaction = rel
			break
		}
	}

	return nil
}

