type purchaseHistoryJoins[Q dialect.Joinable] struct {
	typ                    string
	PurchaseTransaction    modAs[Q, purchaseTransactionColumns]
	IPIntellectualProperty modAs[Q, intellectualPropertyColumns]
}

func (j purchaseHistoryJoins[Q]) aliasedAs(alias string) purchaseHistoryJoins[Q] {
	return buildPurchaseHistoryJoins[Q](buildPurchaseHistoryColumns(alias), j.typ)
}

func buildPurchaseHistoryJoins[Q dialect.Joinable](cols purchaseHistoryColumns, typ string) purchaseHistoryJoins[Q] {
	return purchaseHistoryJoins[Q]{
		typ: typ,
		PurchaseTransaction: modAs[Q, purchaseTransactionColumns]{
			c: PurchaseTransactions.Columns,
			f: func(to purchaseTransactionColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, PurchaseTransactions.Name().As(to.Alias())).On(
						to.ID.EQ(cols.PurchaseTransactionID),
					))
				}

				return mods
			},
		},
		IPIntellectualProperty: modAs[Q, intellectualPropertyColumns]{
			c: IntellectualProperties.Columns,
			f: func(to intellectualPropertyColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, IntellectualProperties.Name().As(to.Alias())).On(
						to.ID.EQ(cols.IPID),
					))
				}

				return mods
			},
		},
	}
}
