func buildUserThenLoader[Q orm.Loadable]() userThenLoader[Q] {
	type ContactsLoadInterface interface {
		LoadContacts(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}
	type CreditCardsLoadInterface interface {
		LoadCreditCards(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}
	type RefreshTokenLoadInterface interface {
		LoadRefreshToken(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}
	type ShippingAddressesLoadInterface interface {
		LoadShippingAddresses(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}

	return userThenLoader[Q]{
		Contacts: thenLoadBuilder[Q](
			"Contacts",
			func(ctx context.Context, exec bob.Executor, retrieved ContactsLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadContacts(ctx, exec, mods...)
			},
		),
		CreditCards: thenLoadBuilder[Q](
			"CreditCards",
			func(ctx context.Context, exec bob.Executor, retrieved CreditCardsLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadCreditCards(ctx, exec, mods...)
			},
		),
		RefreshToken: thenLoadBuilder[Q](
			"RefreshToken",
			func(ctx context.Context, exec bob.Executor, retrieved RefreshTokenLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadRefreshToken(ctx, exec, mods...)
			},
		),
		ShippingAddresses: thenLoadBuilder[Q](
			"ShippingAddresses",
			func(ctx context.Context, exec bob.Executor, retrieved ShippingAddressesLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadShippingAddresses(ctx, exec, mods...)
			},
		),
	}
}

// LoadContacts loads the user's Contacts into the .R struct
func (o *User) LoadContacts(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.Contacts = nil

	related, err := o.Contacts(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, rel := range related {
		rel.R.User = o
	}

	o.R.Contacts = related
	return nil
}

// LoadContacts loads the user's Contacts into the .R struct
func (os UserSlice) LoadContacts(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	contacts, err := os.Contacts(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		o.R.Contacts = nil
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range contacts {

			if !(o.ID == rel.UserID) {
				continue
			}

			rel.R.User = o

			o.R.Contacts = append(o.R.Contacts, rel)
		}
	}

	return nil
}

