type userJoins[Q dialect.Joinable] struct {
	typ               string
	Contacts          modAs[Q, contactColumns]
	CreditCards       modAs[Q, creditCardColumns]
	RefreshToken      modAs[Q, refreshTokenColumns]
	ShippingAddresses modAs[Q, shippingAddressColumns]
}

func (j userJoins[Q]) aliasedAs(alias string) userJoins[Q] {
	return buildUserJoins[Q](buildUserColumns(alias), j.typ)
}

func buildUserJoins[Q dialect.Joinable](cols userColumns, typ string) userJoins[Q] {
	return userJoins[Q]{
		typ: typ,
		Contacts: modAs[Q, contactColumns]{
			c: Contacts.Columns,
			f: func(to contactColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Contacts.Name().As(to.Alias())).On(
						to.UserID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
		CreditCards: modAs[Q, creditCardColumns]{
			c: CreditCards.Columns,
			f: func(to creditCardColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, CreditCards.Name().As(to.Alias())).On(
						to.UserID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
		RefreshToken: modAs[Q, refreshTokenColumns]{
			c: RefreshTokens.Columns,
			f: func(to refreshTokenColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, RefreshTokens.Name().As(to.Alias())).On(
						to.UserID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
		ShippingAddresses: modAs[Q, shippingAddressColumns]{
			c: ShippingAddresses.Columns,
			f: func(to shippingAddressColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, ShippingAddresses.Name().As(to.Alias())).On(
						to.UserID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
	}
}
